  line-height: 44px;
  font-size: 15px;
}
.header-down .tab-header .tool-item:hover {
  background-color: #0251ae;
}
.header-down .tab-header .tool-tab {
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
}
.frame-content .menu {
  height: 100%;
  width: 255px;
  cursor: pointer;
  -webkit-user-select: none;
     -moz-user-select: none;
      -ms-user-select: none;
          user-select: none;
  -webkit-transition: all 300ms;
  transition: all 300ms;
  overflow-y: auto;
  overflow-x: hidden;
}
.frame-content .menu.full-screen {
  position: absolute;
  z-index: 999;
  top: 44px;
}
.frame-content .menu.hidden1 {
  -webkit-transform: translateY(-120%);
          transform: translateY(-120%);
}
.frame-content .menu .menu-item > .nav {
  height: 63px;
  font-size: 14px;
  padding: 20px 0 0 30px;
}
.frame-content .menu .menu-item > .nav > i {
  font-size: 20px;
}
.frame-content .menu .menu-item > .nav > .nav-icon {
  height: 20px;
  margin-top: -6px;
}
.frame-content .menu .menu-item > .nav > .title {
  padding-left: 10px;
}
.frame-content .menu .menu-item > .nav > span.icon {
  float: right;
  position: relative;
  top: 5px;
  right: 30px;
  -webkit-transition: all 300ms;
  transition: all 300ms;
}
.frame-content .menu .menu-item > .nav > span.icon.click-down {
  -webkit-transform: rotate(180deg);
  transform: rotate(180deg);
}
.frame-content .menu .menu-item .sub-item {
  list-style-type: none;
  margin: 0;
  padding: 0;
  -webkit-transition: all 300ms;
  transition: all 300ms;
}
.frame-content .menu .menu-item .sub-item.hidden1 {
  height: 0 !important;
}
.frame-content .menu .menu-item .sub-item.hidden1 li {
  display: none;
}
.frame-content .menu .menu-item .sub-item > li {
  width: 255px;
  height: 55px;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  padding-top: 18px;
}
.frame-content .menu .menu-item .sub-item > li .subitem-icon {
  padding-left: 50px;
}
.frame-content .menu .menu-item .sub-item > li .subitem-icon .nav-icon {
  height: 15px;
}
.frame-content .menu .menu-item .sub-item > li .subitem-title {
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
  padding-left: 10px;
}
.frame-content .menu .menu-item .sub-item > li:hover {
  background-color: #047adf;
}
.frame-content .menu .menu-item .sub-item > li.active {
  background-color: #047adf;
}
.frame-content .content {
  margin: 10px;
  -webkit-box-shadow: 0 0 5px #eaeaea;
  box-shadow: 0 0 5px #eaeaea;
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
  border: 1px solid #dfdfdf;
  height: calc(100% - 15px);
  width: 500px;
  background: white;
  position: relative;
}
.frame-content .content .view-content {
  height: 100%;
  overflow-y: auto;
  overflow-x: hidden;
}
.frame-content .content .view-content > div {
  height: 100%;
}
/*----------------------------
            常用页面样式
 -----------------------------*/
.under-design {
  height: 100%;
  background: #a6c3de;
  text-align: center;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
}
.under-design .page-content {
  margin: auto;
}
.under-design i {
  font-size: 120px;
  color: rgba(0, 0, 0, .2);
  -ms-flex-item-align: center;
      align-self: center;
}
.under-design .title {
  color: #FFF;
  font-size: 60px;
  text-align: center;
}
/*----------------------------
            通用样式
 -----------------------------*/
.btn {
  border-radius: 0;
  min-width: 90px;
}
.ui-grid-top-panel {
  background: #f5f5f5 !important;
}
/*----------------------------
            插件补充样式
 -----------------------------*/
#loading-bar .bar {
  top: auto !important;
  left: auto !important;
  background-color: red;
}
#loading-bar-spinner {
  color: red;
  top: auto !important;
  left: auto !important;
}
.fr-box.fr-basic .fr-element {
  white-space: normal;
  word-break: break-all;
}
/*----------------------------
            兼容低分屏样式
 -----------------------------*/
@media screen and (min-width: 1366px) and (max-width: 1600px) {
  .query-form > header > h4 {
    padding: 10px;
  }
  .ngdialog.ngdialog-theme-default {
    padding-top: 80px;
    padding-bottom: 80px;
  }
}
.pageload-overlay {
  position: absolute;
}
.pageload-overlay::after,
.pageload-overlay::before {
  position: absolute;
}
@media screen and (max-width: 1366px) {
  .frame2 > header {
    height: 114px !important;
  }
  .frame2 > header.full-screen {
    height: 44px !important;
  }
  .header-up {
    height: 70px !important;
  }
  .header-up .system-info .logo img {
    width: 70px !important;
  }
  .header-up .tools .theme-option > span {
    line-height: 70px !important;
  }
  .header-up .user-info > .btn-group .user-img {
    margin-top: 5px !important;
  }
  .header-up .user-info > .btn-group .dropdown-toggle {
    line-height: 70px !important;
    height: 70px !important;
  }
  .header-up.full-screen {
    height: 0 !important;
  }
  .service-header div:nth-child(1) {
    width: 200px !important;
  }
  .service-center .service-tree {
    width: 200px !important;
  }
  .frame-content .menu .menu-item .nav {
    height: 44px !important;
    padding: 12px 0px 0px 30px !important;
  }
  .frame-content .menu .menu-item .sub-item > li {
    height: 44px !important;
    padding-top: 12px !important;
  }
  .query-form header h4 {
    padding: 10px 0px 10px 10px !important;
  }
  .query-form .query-content {
    padding: 0px 8px !important;
  }
  .query-form .query-content .tool-bar {
    height: 44px !important;
  }
  .query-form .query-content .query-condition {
    padding: 8px 0px 0px 0px!important;
  }
  .query-form .query-content .query-condition .query-tool {
    padding-left: 40px !important;
  }
  .query-form .query-content .tool-bar .tool-tag .tag-container {
    margin: auto !important;
  }
  .query-form .query-content .tool-buttons .btn-link {
    font-size: 14px !important;
    border: none !important;
  }
  .query-form .query-content .content-table table > tbody > tr:nth-child(even) > td,
  .query-form .query-content .content-table table > tbody > tr:nth-child(even) > th {
    background-color: #f9f9f9;
  }
  .query-form .query-content .content-table .table-striped > tbody > tr:nth-child(odd) > td,
  .query-form .query-content .content-table .table-striped > tbody > tr:nth-child(odd) > th {
    background-color: #ffffff !important;
  }
  .query-form .query-content .app-content ul li > span > div > div > div.title {
    height: 25px !important;
    line-height: 25px !important;
  }
  .query-form .query-content .app-content ul li > span > div > div > div.image {
    width: 40% !important;
    padding-top: 40% !important;
  }
  .query-form .query-content .app-content ul li > span > div > div > div.image:hover {
    width: 45% !important;
    padding-top: 45% !important;
  }
  .ngdialog.ngdialog-theme-default.custom.sm .ngdialog-content {
    width: 570px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content {
    width: 900px !important;
    height: 450px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .dialog-header {
    padding: 10px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .dialog-content {
    height: 300px !important;
    padding: 0px 30px 10px 30px!important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .nav-tabs {
    margin: 0px 10px;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .gm-pagination .pager-loose {
    margin: 10px 0px !important;
    line-height: 1em;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .gm-pagination .pager-loose > li > a {
    padding: 3px 9px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .gm-pagination .page-tool {
    margin: 13px 0 0 10px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-label {
    padding: 13px 8px 0px 8px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-label label {
    margin-bottom: 0px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-value {
    padding: 13px 50px 0px 8px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-value .form-control {
    height: 25px !important;
    line-height: 1 !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-value .gm-tree-div {
    min-height: 50px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple .table td.td-value .gm-tree-div .tree-a {
    font-size: 13px !important;
  }
  .ngdialog.ngdialog-theme-default.custom .ngdialog-content .form-simple tr.catagory span {
    margin-top: 10px !important;
  }
}
.theme-choosed {
  background: #145ccd;
}
.theme-choosed > a {
  color: #ffffff !important;
}
