            框架样式
 -----------------------------*/
html {
  font-family: "Microsoft Yahei", "Droid Sans Fallback", "Arial", "Helvetica", "sans-serif", "宋体";
  font-size: 14px;
  outline: none;
  color: #373d46;
  background-color: #fafafa;
}
.frame2 {
  height: 100%;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}
.frame2 > header {
  height: 124px;
  z-index: 1;
  user-select: none;
}
.frame2 > header.full-screen {
  height: 44px;
}
.frame2 .frame-content {
  display: flex;
  flex: 1 1 auto;
  height: 1px;
  z-index: 0;
}
.frame2 > footer {
  height: 0;
}
.frame2 .notify {
  position: absolute;
  right: 400px;
  width: 300px;
  align-self: center;
  top: 25px;
}
.frame2 .notify.full-screen {
  top: 6px;
}
.header-up {
  height: 80px;
  display: flex;
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
}
.header-up.full-screen {
  height: 0;
}
.header-up.full-screen * {
  display: none;
}
.header-up .system-info {
  color: #ffffff;
  display: flex;
}
.header-up .system-info .logo img {
  height: 100%;
  width: 80px;
}
.header-up .system-info .system-name {
  padding: 16px 0 0 10px;
}
.header-up .system-info .system-name > div:first-child {
  font-size: 20px;
  font-weight: bold;
}
.header-up .tools {
  flex: 1;
}
.header-up .tools .theme-option {
  height: 100%;
  color: white;
  text-align: right;
  padding-right: 30px;
  display: flex;
}
.header-up .tools .theme-option .dropdown .dropdown-toggle {
  line-height: 80px;
  margin-left: 10px;
  flex: 1;
  padding: 0 10px;
  cursor: pointer;
  height: 80px;
}
.header-up .tools .theme-option .dropdown ul li .theme-item {
  float: left;
  background: red;
  border-radius: 50px;
  padding: 2px;
  border: 2px solid white;
  margin-right: 5px;
  height: 20px;
  width: 20px;
  align-self: center;
  text-align: center;
  cursor: pointer;
}
.header-up .tools .theme-option .dropdown ul li .theme-item i {
  position: relative;
  top: -3px;
}
.header-up .user-info {
  width: 180px;
  color: white;
}
.header-up .user-info > .btn-group {
  display: flex;
}
.header-up .user-info > .btn-group .user-img {
  height: 60px;
  width: 60px;
  background: aliceblue;
  border-radius: 30px;
  margin-top: 10px;
}
.header-up .user-info > .btn-group .user-img > div {
  height: 54px;
  width: 54px;
  background: red;
  border-radius: 30px;
  margin: 3px;
}
.header-up .user-info > .btn-group .dropdown-toggle {
  line-height: 80px;
  margin-left: 10px;
  flex: 1;
  padding: 0 10px;
  cursor: pointer;
  height: 80px;
}
.header-up .user-info > .btn-group .dropdown-toggle > span:first-child {
  max-width: 65px;
  display: inline-block;
  line-height: 80px;
}
.header-up .user-info > .btn-group .dropdown-toggle > span:last-child {
  font-size: 12px;
  padding-left: 5px;
  display: inline-block;
  position: absolute;
  top: 33px;
}
.header-down {
  height: 44px;
  background: #1b5fcc;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.15);
  display: flex;
}
.header-down .menu-header {
  width: 255px;
  background-color: #2d3846;
  text-align: center;
  text-transform: uppercase;
