 -----------------------------*/
html{
  font-family:"Microsoft Yahei","Droid Sans Fallback","Arial","Helvetica","sans-serif","宋体";font-size:14px;outline: none;color: @fontColor;
  background-color: @themeBackground;
}

.frame2{
  height: 100%;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  >header{
    height:@headerHeight;
    z-index: 1;
    user-select: none;
    &.full-screen{
      height:@subHeaderHeight;
    }
  }
  .frame-content{
    display: flex;
    flex:1 1 auto;
    height: 1px;
    z-index: 0;
  }
  >footer{
    height:0;
  }
  .notify{
    position: absolute;
    right: 400px;
    width: 300px;
    align-self: center;
    top: 25px;
  }
  .notify.full-screen{
    top: 6px;
  }
}
.header-up{
  height:@headerHeight - @subHeaderHeight;
  display: flex;
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
  &.full-screen{
    height: 0;
    *{
      display:none;
    }
  }
  .system-info{
    color: @selectFontColor;
    display: flex;
    .logo img{
      height:100%;
      width: 80px;
    }
    .system-name{
      padding: 16px 0 0 10px;
      >div:first-child{
        font-size: 20px;
        font-weight: bold;
      }
    }
  }
  .tools{
    flex:1;
    .theme-option{
      height: 100%;
      color: white;
      text-align: right;
      padding-right: 30px;
      display: flex;
      .dropdown{
          .dropdown-toggle{
            line-height: 80px;
            margin-left: 10px;
