    width: 100px;
    .tab-wrapper{
      height: 100%;
      .tab-tool{
        background: #1382ea;
        &:hover{
          background-color: #0251ae;
        }
        i{
          line-height: 80px;
        }
      }
      .tab-tabs{
        &>ul{
          height:100%;
          li{
            flex-direction: column;
            background: rgb(37,135,236);
            padding: 10px;
            height: 100%;
            margin: 0;
            border-right: solid 1px rgb(4,109,218);
            &>i{
              font-size: 30px;
            }
            .title{
              color: rgb(208,222,248);
            }
            .nav-icon{
              width: 30px;
              margin: auto;
            }
          }
          li:hover,li.active{
            background: rgb(177,215,254);
            &>i{
              color: rgb(4,92,189);
            }
            .title{
              color: rgb(4,92,189);
            }
          }
        }
      }
    }
  }
  .user-info{
    width: 230px;
    color: white;
    position: relative;
    z-index: 9999;
    background: #0e6fea;
    padding: 0 0 0 50px;
    >.btn-group{
      display: flex;
      .user-img{
        height: 60px;
        width: 60px;
        background: aliceblue;
        border-radius: 30px;
        margin-top: 10px;
        >div{
          height: 54px;
          width: 54px;
          background: red;
          border-radius: 30px;
          margin: 3px;
        }
      }
      .dropdown-toggle{
        line-height: 80px;
        margin-left: 10px;
        flex: 1;
        padding: 0 10px;
        cursor: pointer;
        height: 80px;
        >span:first-child{
          max-width: 65px;
          display: inline-block;
          line-height: 80px;
        }
        >span:last-child{
          font-size: 12px;
          padding-left: 5px;
          display: inline-block;
          position: absolute;
          top: 33px;
        }
      }
    }
  }
}
.frame-content{
  &>.menu{
    height:100%;
    width:60px;
    background: rgb(47,58,76);
    >nav{
      height:100%;
      .menu-item{
        height: 75px;
        color: white;
        text-align: center;
        padding: 10px 0;
        cursor: pointer;
        position: relative;
        i{
          font-size: 30px;
        }
        .title{
          width: 60px;
          display: inline-block;
          text-overflow: clip;
          color: #d1d1d3;
          margin-top: 5px;
        }
        .menu-arrow{
          margin: -42px 10px 0 50px;
          border-top: 8px solid transparent;
          border-bottom: 8px solid transparent;
          border-right: 13px solid white;
          position: absolute;
          z-index: 9999;
          display: none;
        }
      }
      .menu-item:hover{
        background: rgb(28,36,49);
      }
      .menu-item.active{
        background: rgb(28,36,49);
        .menu-arrow{
          display: block;
        }
      }
    }
  }
  &>.content{
    height:100%;
    flex:1;
    .map-content{
      position: relative;
      .map-content-left{
        position: absolute;
        height:100%;
        width: 280px;;
        background: white;
        z-index: 9999;
        display: none;
        .map-content-left.show{
          display: block;
        }
        &>div{
          height: 100%;
          &>div{
            height: 100%;
            overflow-y: auto;
            overflow-x: hidden;
          }
        }
      }
      .map-content-right{
        height:100%;
      }
    }
  }
}
