#myBanner{
    display: block;
    width: 100%;
    height: 90%;
    .backImg{
        width: 100%;
        height: 100%;
    }
    .title{
        position: absolute;
        z-index: 100;
        color: #fff;
        width: 100%;
        height: 125px;
        top: 5%;
        text-align: center;
        .logBox{
            display: inline-block;
            width:120px;
            height: 120px;
            img{
                display: block;
                width: 100%;
                height: auto;
                padding-top: 26px;
            }
        }
        .titleText{
            display: inline-block;
            p:nth-of-type(1){
                font: 40px/50px "微软雅黑";
                text-shadow: 0 2px 5px #000;
            }
            p:nth-of-type(2){
                font: 16px/20px "微软雅黑";
                opacity: .6;
                filter: alpha(opacity=60);
            }
        }
    }
    .logOut{
        position: absolute;
        right: 0;
        top: 5px;
        z-index: 100;
        cursor: pointer;
        .userImg{
            width: 35px;
            height: 35px;
            margin-right: 18px;
        }
        .userName{
            margin-right: 6px; 
            color: #fff;
        }
        .icon-chevron-down{
            color: #fff;
            margin-right: 10px;
        }
        .message{
            padding-left: 0;
            margin-top: 10px;
            list-style: none;
            li{
                line-height: 20px;
                text-align: center;
            }
        }
    }
}
#swiperBox{
    position: absolute;
    top: 35%;
    width:100%;
    height: 35%;
    .swiper-container{
        width: 100%;
        height: 100%;
        .swiper-wrapper{
            .swiper-slide{
                height: 100%;
                cursor: pointer;
                .imgBox{
                    width: 100%;
                    height: 100%;
                    img{
                        transition: all .3s ease-in-out;
                        width: 100%;
                        height: auto;
                        display: block;
                        transform: scale(1.2);
                        -webkit-transform: scale(1.2);
                        -moz-transform: scale(1.2);
                        -ms-transform:scale(1.2);
                    }
                }
                .txtBox{
                    position: absolute;
                    width: 100%;
                    top: 70%;
                    text-align: center;
                    line-height: 20px;
                    font-weight: bold;
                    transition: all .3s ease-in-out; 
                    span{
                        color: #fff;
                        padding: 2px 10%;
                        background: rgba(7,39,57,.8);
                        border-radius:11px;
                    }
                }
            }
            .swiper-slide:nth-of-type(1),.swiper-slide:nth-last-child(1){
                overflow: hidden;
            }
            .swiper-slide:hover{
                .imgBox{
                    img{
                        transform: scale(1.4);
                        -webkit-transform: scale(1.4);
                        -moz-transform: scale(1.4);
                        -ms-transform:scale(1.4);
                    }
                }
                .txtBox{
                    transform: scale(1.2);
                    -webkit-transform: scale(1.2);
                    -moz-transform: scale(1.2);
                    -ms-transform:scale(1.2);
                }
            }
        }
        .center{
            justify-content:center;
        }
        .swiper-button-prev{
            z-index: 100;
            opacity: .8;
            filter:alpha(opacity=8);
            background-image: url(prev.png);
            transform: scale(1.5);
        }
        .swiper-button-next{
            z-index: 100;
            opacity: .8;
            filter:alpha(opacity=8);
            background-image: url(next.png);
            transform: scale(1.5);
        }
    }
}
footer{
    width: 100%;
    height:10%;
    text-align: center;
    line-height: 60px;
    font-size:14px;
}
.maxHeight{
    height: 100%;
