.box {
  display: table;
  visibility: hidden;
  -webkit-transform: perspective(1200px) rotateY(180deg) scale(0.1);
  transform: perspective(1200px) rotateY(180deg) scale(0.1);
  top: 0;
  left: 0;
  z-index: -1;
  position: absolute;
  width: 100%;
  height: 100%;
  opacity: 0;
  -webkit-transition: 1s all;
  transition: 1s all;
  background: #b7deed;
  /* Old browsers */
  /* FF3.6-15 */
  /* Chrome10-25,Safari5.1-6 */
  background: linear-gradient(45deg, #b7deed 0%, #71ceef 37%, #21b4e2 50%, #b7deed 100%);
  /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
  filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#b7deed', endColorstr='#b7deed', GradientType=1);
  /* IE6-9 fallback on horizontal gradient */
}
.box .close {
  display: block;
  cursor: pointer;
  border: 3px solid #ffffff;
  border-radius: 50%;
  position: absolute;
  top: 50px;
  right: 50px;
  width: 50px;
  height: 50px;
  -webkit-transform: rotate(45deg);
  transform: rotate(45deg);
  -webkit-transition: .2s;
  transition: .2s;
  -webkit-transition-delay: 0.2s;
  transition-delay: 0.2s;
  opacity: 0;
}
.box .close:active {
  top: 51px;
}
.box .close::before {
  content: "";
  display: block;
  position: absolute;
  background-color: #ffffff;
  width: 80%;
  height: 6%;
  left: 10%;
  top: 47%;
}
.box .close::after {
  content: "";
  display: block;
  position: absolute;
  background-color: #ffffff;
  width: 6%;
  height: 80%;
  left: 47%;
  top: 10%;
}
.open {
  left: 0;
  top: 0;
  visibility: visible;
  opacity: 1;
  z-index: 999;
  -webkit-transform: perspective(1200px) rotateY(0deg) scale(1);
  transform: perspective(1200px) rotateY(0deg) scale(1);
  width: 100%;
  height: 100%;
}
.open .close {
  opacity: 1;
}
footer {
