        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#007d93', endColorstr='#009faf',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    }
    .subBox:nth-of-type(9){
        background: rgb(35,110,227); /* Old browsers */
        background: -moz-linear-gradient(45deg, rgba(35,110,227,1) 33%, rgba(43,129,238,1) 74%, rgba(46,140,240,1) 100%); /* FF3.6-15 */
        background: -webkit-linear-gradient(45deg, rgba(35,110,227,1) 33%,rgba(43,129,238,1) 74%,rgba(46,140,240,1) 100%); /* Chrome10-25,Safari5.1-6 */
        background: linear-gradient(45deg, rgba(35,110,227,1) 33%,rgba(43,129,238,1) 74%,rgba(46,140,240,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#236ee3', endColorstr='#2e8cf0',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    }

}
.box {
    display: table;
    top: 0;
    visibility: hidden;
    -webkit-transform: perspective(1200px) rotateY(180deg) scale(0.1);
    -ms-transform: perspective(1200px) rotateY(180deg) scale(0.1);
    transform: perspective(1200px) rotateY(180deg) scale(0.1);
    top: 0;
    left: 0;
    z-index: -1;
    position: absolute;
    width: 100%;
    height: 100%;
    opacity: 0;
    transition: 1s all;
    background: rgb(183,222,237); /* Old browsers */
    background: -moz-linear-gradient(45deg, rgba(183,222,237,1) 0%, rgba(113,206,239,1) 37%, rgba(33,180,226,1) 50%, rgba(183,222,237,1) 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(45deg, rgba(183,222,237,1) 0%,rgba(113,206,239,1) 37%,rgba(33,180,226,1) 50%,rgba(183,222,237,1) 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(45deg, rgba(183,222,237,1) 0%,rgba(113,206,239,1) 37%,rgba(33,180,226,1) 50%,rgba(183,222,237,1) 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#b7deed', endColorstr='#b7deed',GradientType=1 ); /* IE6-9 fallback on horizontal gradient */
    .close {
      display:block;
      cursor:pointer;
      border:3px solid rgba(255, 255, 255, 1);
      border-radius:50%;
      position:absolute;
      top:50px;
      right:50px;
      width:50px;
      height:50px;
      -webkit-transform:rotate(45deg);
      -ms-transform:rotate(45deg);
      transform:rotate(45deg);
      transition: .2s;
      -webkit-transition-delay: 0.2s;
      -ms-transition-delay: 0.2s;
      transition-delay: 0.2s;
      opacity:0;
    }
    .close:active {
        top:51px; 
    }
    .close::before {
      content: "";
      display: block;
      position: absolute;
      background-color: rgba(255, 255, 255, 1);
      width: 80%;
      height: 6%;
      left: 10%;
      top: 47%;
    }
    .close::after {
      content: "";
      display: block;
      position: absolute;
      background-color: rgba(255, 255, 255, 1);
      width: 6%;
      height: 80%;
      left: 47%;
      top: 10%;
    }
}
.open {
    left: 0;
    top: 0;
    visibility: visible;
    opacity: 1;
    z-index: 999;
    -webkit-transform: perspective(1200px) rotateY(0deg) scale(1);
    -ms-transform: perspective(1200px) rotateY(0deg) scale(1);
    transform: perspective(1200px) rotateY(0deg) scale(1);
    width: 100%;
    height: 100%;
    .close{
        opacity: 1;
    }
}
footer{
    width: 100%;
