  display: inline-block;
  text-align: center;
}
.tag-container .tag-item .angle {
  border-bottom: 15px solid #e7e7e7;
  border-left: 15px solid transparent;
  height: 0;
  width: 0;
  position: absolute;
  margin-top: 8px;
  margin-left: -5px;
}
.tag-container .tag-item .icon {
  position: absolute;
  margin-top: 13px;
  font-size: 12px;
  -webkit-transform: scale(0.9);
          transform: scale(0.9);
  color: white;
  margin-left: -2px;
}
.tag-container .extra-tags-tool {
  -webkit-user-select: none;
     -moz-user-select: none;
      -ms-user-select: none;
          user-select: none;
  height: 30px;
  padding: 5px;
  cursor: pointer;
  white-space: nowrap;
}
.tag-container .extra-tags-tool > span.rotate {
  -webkit-transform: rotate(180deg);
  transform: rotate(180deg);
  -webkit-transition: all 300ms;
  transition: all 300ms;
}
.tag-container .extra-tags-tool .extra-tags {
  position: absolute;
  width: 420px;
  margin-top: 10px;
  margin-left: -170px;
  -webkit-box-shadow: 0 2px 5px rgba(0, 0, 0, .15);
          box-shadow: 0 2px 5px rgba(0, 0, 0, .15);
  background: white;
  padding: 20px 10px 10px 20px;
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  -ms-flex-flow: wrap;
      flex-flow: wrap;
  z-index: 1;
}
