}
/*--------------------
页面通用小控件模块-轮播通知
 ---------------------*/
.carousel-container {
  display: -webkit-box;
  display: -ms-flexbox;
  display: flex;
  padding: 5px;
}
.carousel-container .icon-div {
  background: url(" + escape(require("./img/公告.png")) + ");
  height: 18px;
  width: 20px;
  margin: 0 10px 0 0;
}
.carousel-container .text-div {
  -webkit-box-flex: 1;
      -ms-flex: 1;
          flex: 1;
  height: 18px;
  width: 10px;
  position: relative;
  overflow: hidden;
}
.carousel-container .text-div .swap-animation {
  font-size: 14px;
  line-height: 18px;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  color: white;
}
.carousel-container .text-div .swap-animation span {
  word-break: keep-all;
  white-space: nowrap;
  position: absolute;
  -webkit-transition: 0.5s linear all;
  transition: 0.5s linear all;
}
.carousel-container .text-div .swap-animation.ng-enter {
  top: -50px;
}
.carousel-container .text-div .swap-animation.ng-enter-active {
  top: 0;
}
.carousel-container .text-div .swap-animation.ng-leave {
  top: 0;
}
.carousel-container .text-div .swap-animation.ng-leave-active {
  top: 50px;
}
/*--------------------
       轮播插件
 ---------------------*/
body .rn-carousel-control {
  font-size: 60px;
  color: wheat;
}
/*--------------------
       滑块插件
 ---------------------*/
.ui-slider-tick {
  position: absolute;
  background-color: #aaa;
  width: 2px;
  height: 8px;
  top: 16px;
}
.ui-slider {
  position: relative;
}
.ui-slider-out-of-bounds {
  position: absolute;
  background-color: #EEE;
  height: 100%;
}
.ui-slider-tip {
  position: absolute;
  top: 2px;
  left: 0px;
  width: 21px;
  text-align: center;
  font-size: 0.8em;
}
/*--------------------
       拖拽事件
 ---------------------*/
[ng-drag="true"] {
  cursor: move;
