}
.gm-tree-div .icont-folder-open {
  line-height: 0;
  margin: 0;
  padding: 0;
  width: 21px;
  height: 21px;
  display: inline-block;
  vertical-align: middle;
  border: 0 none;
  cursor: pointer;
  outline: none;
  background-color: transparent;
  background-repeat: no-repeat;
  background-attachment: scroll;
  background-image: url("img/metro.png");
  vertical-align: top;
  background-image: url("img/folder-open.png");
  width: 14px;
  height: 14px;
  margin: 3px 0 0 0;
}
.gm-tree-div .icont-folder-close {
  line-height: 0;
  margin: 0;
  padding: 0;
  width: 21px;
  height: 21px;
  display: inline-block;
  vertical-align: middle;
  border: 0 none;
