 ---------------------*/
.tag-container {
  display: flex;
}
.tag-container > button {
  min-width: auto;
  margin: 5px 10px 0 0;
  height: 21px;
}
.tag-container .tag-item {
  height: 30px;
  border: 1px solid #e7e7e7;
  margin-right: 10px;
  cursor: pointer;
  display: flex;
  padding: 5px 10px;
  position: relative;
}
.tag-container .tag-item:hover {
  border-color: #037ae0;
}
.tag-container .tag-item.checked {
  border-color: #037ae0;
}
.tag-container .tag-item.checked > span {
  color: #037ae0;
}
.tag-container .tag-item.checked .angle {
  border-bottom-color: #037ae0;
}
.tag-container .tag-item > span {
  display: inline-block;
  text-align: center;
}
.tag-container .tag-item .angle {
  border-bottom: 15px solid #e7e7e7;
  border-left: 15px solid transparent;
  height: 0;
  width: 0;
  position: absolute;
  margin-top: 8px;
  margin-left: -5px;
}
.tag-container .tag-item .icon {
  position: absolute;
  margin-top: 13px;
  font-size: 12px;
  transform: scale(0.9);
  color: white;
  margin-left: -2px;
}
.tag-container .extra-tags-tool {
  user-select: none;
  height: 30px;
  padding: 5px;
  cursor: pointer;
  white-space: nowrap;
}
.tag-container .extra-tags-tool > span.rotate {
  -webkit-transform: rotate(180deg);
  -ms-transform: rotate(180deg);
  -o-transform: rotate(180deg);
  transform: rotate(180deg);
  -webkit-transition: all 300ms;
  -moz-transition: all 300ms;
  -ms-transition: all 300ms;
  -o-transition: all 300ms;
  transition: all 300ms;
}
.tag-container .extra-tags-tool .extra-tags {
  position: absolute;
  width: 420px;
  margin-top: 10px;
  margin-left: -170px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.15);
  background: white;
  padding: 20px 10px 10px 20px;
  display: flex;
  flex-flow: wrap;
  z-index: 1;
}
.date-choose-div {
  position: relative;
}
.date-choose-div .icon {
  position: absolute;
  right: 5px;
  top: 9px;
}
/*--------------------
页面通用小控件模块-轮播通知
 ---------------------*/
.carousel-container {
  display: flex;
  padding: 5px;
}
.carousel-container .icon-div {
  background: url(img/公告.png);
  height: 18px;
  width: 20px;
  margin: 0 10px 0 0;
}
.carousel-container .text-div {
  flex: 1;
  height: 18px;
  width: 10px;
  position: relative;
  overflow: hidden;
}
.carousel-container .text-div .swap-animation {
  font-size: 14px;
  line-height: 18px;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  color: white;
}
.carousel-container .text-div .swap-animation span {
  word-break: keep-all;
  white-space: nowrap;
  position: absolute;
  transition: 0.5s linear all;
}
.carousel-container .text-div .swap-animation.ng-enter {
  top: -50px;
}
.carousel-container .text-div .swap-animation.ng-enter-active {
  top: 0;
}
.carousel-container .text-div .swap-animation.ng-leave {
  top: 0;
}
.carousel-container .text-div .swap-animation.ng-leave-active {
  top: 50px;
}
/*--------------------
       轮播插件
 ---------------------*/
body .rn-carousel-control {
  font-size: 60px;
  color: wheat;
}
/*--------------------
       滑块插件
 ---------------------*/
.ui-slider-tick {
  position: absolute;
  background-color: #aaa;
  width: 2px;
  height: 8px;
  top: 16px;
}
.ui-slider {
  position: relative;
}
.ui-slider-out-of-bounds {
  position: absolute;
  background-color: #EEE;
  height: 100%;
}
.ui-slider-tip {
  position: absolute;
  top: 2px;
  left: 0px;
  width: 21px;
  text-align: center;
  font-size: 0.8em;
}
/*--------------------
       拖拽事件
 ---------------------*/
[ng-drag="true"] {
  cursor: move;
}
