  background-color: transparent;
  background-repeat: no-repeat;
  background-attachment: scroll;
  background-image: url("img/metro.png");
  background-position: -26px -5px;
  width: 13px;
  height: 13px;
  margin: 4px 2px 0 0;
  background-position: -5px -5px;
}
.gm-tree-div .icon-check-node {
  line-height: 0;
  margin: 0;
  padding: 0;
  width: 21px;
  height: 21px;
  display: inline-block;
  vertical-align: middle;
  border: 0 none;
  cursor: pointer;
  outline: none;
  background-color: transparent;
  background-repeat: no-repeat;
  background-attachment: scroll;
  background-image: url("img/metro.png");
  background-position: -26px -5px;
  width: 13px;
  height: 13px;
  margin: 4px 2px 0 0;
}
.gm-tree-div .icont-folder-open {
  line-height: 0;
  margin: 0;
  padding: 0;
  width: 21px;
  height: 21px;
  display: inline-block;
  vertical-align: middle;
  border: 0 none;
  cursor: pointer;
  outline: none;
  background-color: transparent;
  background-repeat: no-repeat;
  background-attachment: scroll;
  background-image: url("img/metro.png");
  vertical-align: top;
  background-image: url("img/folder-open.png");
  width: 14px;
  height: 14px;
  margin: 3px 0 0 0;
}
.gm-tree-div .icont-folder-close {
  line-height: 0;
  margin: 0;
  padding: 0;
  width: 21px;
  height: 21px;
  display: inline-block;
  vertical-align: middle;
  border: 0 none;
  cursor: pointer;
  outline: none;
  background-color: transparent;
  background-repeat: no-repeat;
  background-attachment: scroll;
  background-image: url("img/metro.png");
  background-image: url("img/folder-close.png");
  vertical-align: top;
  width: 14px;
  height: 14px;
  margin: 3px 0 0 0;
}
.gm-tree-div .icont-file {
  line-height: 0;
  margin: 0;
